import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    data: [],
  },
  getters: {
    async increment(state){
      const res = await fetch("https://jsonplaceholder.typicode.com/users");
      const json = await res.json();
      this.$store.state.data = json;
    }
  },
  mutations: {


  },
  actions: {
  },
  modules: {
  }
})
